using UnityEngine;using UnityEditor;using UnityEditor.SceneManagement;[InitializeOnLoad]public class GregDontTouch : MonoBehaviour{private const float frogInterval = 1f;private static float frogIntCheck; static GregDontTouch(){EditorApplication.update += THEFROGSHALLLIVE;frogIntCheck = Time.realtimeSinceStartup;}private static bool graveMistake;private static bool frogCheck = false;private static bool deadFrog = false;private static void THEFROGSHALLLIVE(){if (!frogCheck){string[] alive = AssetDatabase.FindAssets("GregsFrog t:Sprite");if (alive.Length == 0){string[] emergencyFrog = AssetDatabase.FindAssets("Clone t:Sprite");if (emergencyFrog.Length > 0){bool revival = AssetDatabase.CopyAsset(AssetDatabase.GUIDToAssetPath(emergencyFrog[0]), "Assets/GregsFrog.png");if (!revival){deadFrog = true;}AssetDatabase.Refresh();}else{deadFrog = true;}}frogCheck = true;}if (!deadFrog && Time.realtimeSinceStartup - frogIntCheck >= frogInterval){if (graveMistake){string[] emergencyFrog = AssetDatabase.FindAssets("Clone t:Sprite");if (emergencyFrog.Length > 0){bool revival = AssetDatabase.CopyAsset(AssetDatabase.GUIDToAssetPath(emergencyFrog[0]), "Assets/GregsFrog.png");if (revival){graveMistake = false;}else{deadFrog = true;}AssetDatabase.Refresh();}else{deadFrog = true;}}else{string[] alive = AssetDatabase.FindAssets("GregsFrog t:Sprite");if (alive.Length == 0){graveMistake = true;}}frogIntCheck = Time.realtimeSinceStartup;}}}